function NoNegDec(nbr) {
var Integer = Math.abs(Math.round(nbr));
var Positive = Math.abs(Integer);
return(Positive);
}

function forceright(field,length) {
    var str = "" + field;
    while (str.length < length)
        str = " " + str;
    return str;
}

function CurrencyDisplay(num,length2) {
var fieldlength = length2;
var displayedVersion = (Math.round(num * 100) / 100);
if (displayedVersion == Math.round(num))
{
displayedVersion = displayedVersion + ".00";
}
else if ((10 * displayedVersion) == Math.round(num * 10))
{
displayedVersion = displayedVersion + "0";
}
return(forceright(displayedVersion,fieldlength));
}

function CalcShipping(form) {
var Qtya_Item1 = NoNegDec(document.orderform.QtyItem1.value);
var Qtya_Item2 = NoNegDec(document.orderform.QtyItem2.value);
//var Qtya_Item3 = NoNegDec(document.orderform.QtyItem3.value);

var SH = 0;
if (Qtya_Item1 + Qtya_Item2 > 0) {
	if (document.orderform.ShipWay[0].checked) {
		SH = (26.95 + (Qtya_Item1 * 15) );
	} else if (document.orderform.ShipWay[1].checked) {
		SH = (34.95 + (Qtya_Item1 * 20));
	} else if (document.orderform.ShipWay[2].checked) {
		SH = (44.95 + (Qtya_Item1 * 30));
	} else {
		SH = (89.95 + (Qtya_Item1 * 45));
	}
}
return(SH);
}


function computeTotal(form) {

    if (form.DescItem4.value == 'Tourweaver Std') {
        var Item4Desc = new Array("Tourweaver Std", 269.95, "Cam", "Sales:Camera Sales");
    } else if (form.DescItem4.value == 'Tourweaver Pro') {
        var Item4Desc = new Array("Tourweaver Pro", 799.95, "Cam", "Sales:Camera Sales");
    } else if (form.DescItem4.value == 'Panoweaver Std') {
        var Item4Desc = new Array("Panoweaver Std", 134.95, "Cam", "Sales:Camera Sales");
    } else if (form.DescItem4.value == 'Panoweaver Pro') {
        var Item4Desc = new Array("Panoweaver Pro", 359.95, "Cam", "Sales:Camera Sales");
    } else if (form.DescItem4.value == 'Studio 2015') {
        var Item4Desc = new Array("Studio 2015", 989.95, "Cam", "Sales:Camera Sales");
    }


    document.orderform.PriceItem1.value = CurrencyDisplay(295,7);
    document.orderform.PriceItem4.value = CurrencyDisplay(Item4Desc[1],7);
    document.orderform.PartNumItem4.value = Item4Desc[2];
    document.orderform.SalesAcctItem4.value = Item4Desc[3];

    var SubT = 0;

        var Qty_Item1 = NoNegDec(document.orderform.QtyItem1.value);
        var Item1_Price = document.orderform.PriceItem1.value;
        var Total_Item1 = (Qty_Item1 * Item1_Price);
        SubT = SubT + Total_Item1;

        var Qty_Item2 = NoNegDec(document.orderform.QtyItem2.value);
        var Item2_Price = document.orderform.PriceItem2.value;
        var Total_Item2 = (Qty_Item2 * Item2_Price);
        SubT = SubT + Total_Item2;

        var Qty_Item4 = NoNegDec(document.orderform.QtyItem4.value);
        var Item4_Price = document.orderform.PriceItem4.value;
        var Total_Item4 = (Qty_Item4 * Item4_Price);
        SubT = SubT + Total_Item4;

    var SnH = CalcShipping(form);
    var Tot = SubT + SnH;

        document.orderform.QtyItem1.value = Qty_Item1;
        document.orderform.PriceItem1.value = CurrencyDisplay(Item1_Price,7);
        document.orderform.TotalItem1.value = CurrencyDisplay(Total_Item1,12);

        document.orderform.QtyItem2.value = Qty_Item2;
        document.orderform.PriceItem2.value = CurrencyDisplay(Item2_Price,7);
        document.orderform.TotalItem2.value = CurrencyDisplay(Total_Item2,12);

        document.orderform.QtyItem4.value = Qty_Item4;
        document.orderform.PriceItem4.value = CurrencyDisplay(Item4_Price,7);
        document.orderform.TotalItem4.value = CurrencyDisplay(Total_Item4,12);

    document.orderform.SubTotal.value = CurrencyDisplay(SubT,12);
    document.orderform.Shipping.value = CurrencyDisplay(SnH,12);
    document.orderform.Total.value = CurrencyDisplay(Tot,12);

}

