.entries:not([data-cards=cover]) .entry-button {
    white-space: nowrap;
}

.wp-block-image img {
border-radius:0.5rem;
}



/* Image Center */
.wp-block-image {
    text-align:center;

}

/* Header Style */
h4,h5,h6{
text-align:center !important;
border-radius:0.5rem;
/* background-color:#011638; */
}

/* Kadence Rating Box Auto*/
.kt-testimonial-grid-wrap .kt-testimonial-item-wrap {
    margin: 0 0 0 0;
}

/* Image Text Caption Color */
.wp-block-image figcaption {
	color:#BABABA !important;
}

/* Video Embed Center */
.wp-block-embed {
    margin-left: auto !important;
    margin-right: auto !important;
}

h1,h2,h3,h4 {
background-image: linear-gradient(180deg, #e51c23  0%, #e84e40  100%);
padding: 5px;
text-align:center !important;	
border-radius:15px;
 box-shadow: 0px 0px 5px 0px #e51c23;
	}


.sc_card {
		background: none;
		box-shadow: none;
}

p{
	padding:10px
}


a.ct-button{
font-weight: var(--e-global-typography-8ef90f9-font-weight );
    line-height: var(--e-global-typography-8ef90f9-line-height );
    letter-spacing: var(--e-global-typography-8ef90f9-letter-spacing );
    word-spacing: var(--e-global-typography-8ef90f9-word-spacing );
    fill: #FFFFFF;
    background-color: transparent;
    text-align: center !important;
    background-image: linear-gradient(180deg, #e51c23  0%, #e84e40  100%);
    border-radius: 0.6rem
    padding: 0.5rem;
    border-style: none;
    border-radius: 10px 10px 10px 10px;
    box-shadow: 0px 0px 5px 0px #e51c23 ;
    padding: px 8px 2px 8px;
}

section.sc_fs_faq.sc_card{
	text-align:center !important;	
}


[data-menu*=type-3]>ul>li:hover>a, [data-menu*=type-3]>ul>li[class*=current-menu-]>a{
fill: #FFFFFF;
color: #FFFFFF;
 background-color: transparent;
background-image: linear-gradient(180deg, 	 #e51c23  0%, #e84e40  100%);
 border-radius: 0.6rem
 border-radius: 7px 7px 7px 7px;
  box-shadow: 0px 0px 5px 0px #e51c23;
 }

.ct-shortcuts-container.ct-hidden-lg{
	background-image: linear-gradient(180deg, #e51c23  0%, #e84e40  100%);
box-shadow: 0px 0px 5px 0px #e51c23;
}

.ct-shortcuts-container.ct-hidden-lg{
	background-image: linear-gradient(180deg, #e51c23  0%, #e84e40  100%);
	box-shadow: 0px 0px 10px 0px #e51c23;
}


@media (max-width: 999.98px)
[data-header*="type-1"] [data-id="trigger"] {
    --icon-size: 15px;
    --icon-color: #DD4A08;
    --icon-hover-color: var(--theme-palette-color-4);
    --secondColor: var(--theme-palette-color-4);
    --secondColorHover: #DD4A08;

}

.ct-shortcuts-container{
padding:0px !important;
	white-space: nowrap;
gap:10px !important;
}

.ct-shortcuts-bar[data-type=type-2] .ct-shortcuts-bar-items {
background-color: transparent;
background-image: linear-gradient(180deg, #e51c23  0%, #e84e40  100%);
	box-shadow: 0px 0px 10px 0px #e51c23
}


[data-link=type-2] .entry-content p>a, [data-link=type-2] .entry-content em>a, [data-link=type-2] .entry-content strong>a, [data-link=type-2] .entry-content>ul a, [data-link=type-2] .entry-content>ol a, [data-link=type-2] .wp-block-table a, [data-link=type-2] .ct-link-styles {
    --textDecoration: underline;
    font-size: 19px;
    font-weight: 900;
    text-underline-offset: 3px;
    text-decoration-thickness: 3px;
    padding: 0px 3px 0px 3px;
    text-transform: uppercase;
    text-shadow: 1px 1px 16px;
}

.ct-header-cta [data-size=small] {
    justify-content: center;
}



.wp-block-rank-math-toc-block{
font-weight:600;
}




