function submitform() {
    var errorDiv = document.getElementById('errorMessage');
    errorDiv.style.display = "none";
    var errorMsg = '';
    if (
        document.orderform.Email.value == undefined || 
        document.orderform.Email.value.replace(/\s/g,'') == '' ||
